// Symbol mapping for Yahoo Finance and TradingView
const symbolMapping = {
  // Commodities
  'XAUUSD': { yahoo: 'GC=F', tradingview: 'OANDA:XAUUSD', name: 'Gold', category: 'Commodity' },
  'XAGUSD': { yahoo: 'SI=F', tradingview: 'OANDA:XAGUSD', name: 'Silver', category: 'Commodity' },

  // Cryptocurrencies
  'BTCUSD': { yahoo: 'BTC-USD', tradingview: 'BINANCE:BTCUSDT', name: 'Bitcoin', category: 'Cryptocurrency' },
  'ETHUSD': { yahoo: 'ETH-USD', tradingview: 'BINANCE:ETHUSDT', name: 'Ethereum', category: 'Cryptocurrency' },
  'XRPUSD': { yahoo: 'XRP-USD', tradingview: 'BINANCE:XRPUSDT', name: 'Ripple', category: 'Cryptocurrency' },
  'XLMUSD': { yahoo: 'XLM-USD', tradingview: 'BINANCE:XLMUSDT', name: 'Stellar', category: 'Cryptocurrency' },
  'SOLUSD': { yahoo: 'SOL-USD', tradingview: 'BINANCE:SOLUSDT', name: 'Solana', category: 'Cryptocurrency' },

  // Forex Pairs
  'EURUSD': { yahoo: 'EURUSD=X', tradingview: 'FX:EURUSD', name: 'EUR/USD', category: 'Forex' },
  'GBPUSD': { yahoo: 'GBPUSD=X', tradingview: 'FX:GBPUSD', name: 'GBP/USD', category: 'Forex' },
  'USDJPY': { yahoo: 'USDJPY=X', tradingview: 'FX:USDJPY', name: 'USD/JPY', category: 'Forex' },
  'AUDUSD': { yahoo: 'AUDUSD=X', tradingview: 'FX:AUDUSD', name: 'AUD/USD', category: 'Forex' },
  'USDCAD': { yahoo: 'USDCAD=X', tradingview: 'FX:USDCAD', name: 'USD/CAD', category: 'Forex' },

  // FANG Stocks
  'MSFT': { yahoo: 'MSFT', tradingview: 'NASDAQ:MSFT', name: 'Microsoft', category: 'Stock' },
  'AAPL': { yahoo: 'AAPL', tradingview: 'NASDAQ:AAPL', name: 'Apple', category: 'Stock' },
  'AMZN': { yahoo: 'AMZN', tradingview: 'NASDAQ:AMZN', name: 'Amazon', category: 'Stock' },
  'GOOGL': { yahoo: 'GOOGL', tradingview: 'NASDAQ:GOOGL', name: 'Google', category: 'Stock' },
  'TSLA': { yahoo: 'TSLA', tradingview: 'NASDAQ:TSLA', name: 'Tesla', category: 'Stock' }
};

// Time-based symbol selection
const hour = new Date().getUTCHours();
let selectedSymbols = [];

if (hour >= 0 && hour < 6) {
  selectedSymbols = ['BTCUSD', 'ETHUSD', 'SOLUSD', 'XRPUSD'];
} else if (hour >= 6 && hour < 12) {
  selectedSymbols = ['EURUSD', 'GBPUSD', 'XAUUSD', 'USDJPY'];
} else if (hour >= 12 && hour < 18) {
  selectedSymbols = ['AAPL', 'MSFT', 'BTCUSD', 'ETHUSD'];
} else {
  selectedSymbols = ['XAUUSD', 'XAGUSD', 'AMZN', 'SOLUSD'];
}

// Create output array
return selectedSymbols.map(symbol => ({
  symbol: symbol,
  yahooSymbol: symbolMapping[symbol].yahoo,
  tradingviewSymbol: symbolMapping[symbol].tradingview,
  name: symbolMapping[symbol].name,
  category: symbolMapping[symbol].category,
  date: new Date().toISOString().split('T')[0],
  timestamp: new Date().toISOString()
}));

// Process Yahoo Finance data
const inputData = $input.all();
const results = [];

for (const item of inputData) {
  const chart = item.json.chart;

  if (!chart || !chart.result || !chart.result[0]) {
    continue;
  }

  const result = chart.result[0];
  const meta = result.meta;
  const quotes = result.indicators.quote[0];

  const currentPrice = meta.regularMarketPrice;
  const previousClose = meta.previousClose;
  const change = currentPrice - previousClose;
  const changePercent = ((change / previousClose) * 100).toFixed(2);

  const highs = quotes.high.filter(v => v !== null);
  const lows = quotes.low.filter(v => v !== null);

  const fiveDayHigh = Math.max(...highs);
  const fiveDayLow = Math.min(...lows);

  const trend = changePercent > 0 ? 'bullish' : 'bearish';
  const trendEmoji = changePercent > 0 ? 'üìà' : 'üìâ';

  let priceDecimals = 2;
  const category = item.json.category;
  if (category === 'Cryptocurrency') {
    priceDecimals = currentPrice > 1000 ? 0 : currentPrice > 1 ? 2 : 6;
  }

  results.push({
    symbol: item.json.symbol,
    yahooSymbol: item.json.yahooSymbol,
    tradingviewSymbol: item.json.tradingviewSymbol,
    name: item.json.name,
    category: category,
    currentPrice: typeof currentPrice === "number" ? currentPrice.toFixed(priceDecimals) : null,
    previousClose: typeof previousClose === "number" ? previousClose.toFixed(priceDecimals) : null,
    change: typeof change === "number" ? change.toFixed(priceDecimals) : null,
    changePercent: changePercent,
    fiveDayHigh: typeof fiveDayHigh === "number" ? fiveDayHigh.toFixed(priceDecimals) : null,
    fiveDayLow: typeof fiveDayLow === "number" ? fiveDayLow.toFixed(priceDecimals) : null,
    support: typeof fiveDayLow === "number" ? fiveDayLow.toFixed(priceDecimals) : null,
    resistance: typeof fiveDayHigh === "number" ? fiveDayHigh.toFixed(priceDecimals) : null,
    trend: trend,
    trendEmoji: trendEmoji,
    timestamp: new Date().toISOString(),
    date: new Date().toISOString().split('T')[0]
  });
}

return results;

