const items = $input.all();
const allMarketData = $('Process Market Data').all().map(i => i.json);
const results = [];

items.forEach((item, index) => {
    const aiContent = item.json.message?.content || item.json.choices?.[0]?.message?.content || '';
    const marketData = allMarketData[index] || allMarketData[0];

    // Extract title
    const titleMatch = aiContent.match(/<h1[^>]*>([^<]+)<\/h1>/);
    const title = titleMatch ? titleMatch[1].trim() : `${marketData.name} Analysis - ${marketData.date}`;

    // Extract meta description
    const metaMatch = aiContent.match(/<p[^>]*>([^<]+)<\/p>/);
    const metaDescription = metaMatch 
        ? metaMatch[1].trim().substring(0, 160)
        : `${marketData.name} price analysis: $${marketData.currentPrice}, ${marketData.trend} trend with ${marketData.changePercent}% change.`;

    // Build slug
    const slug = `${(marketData.symbol || 'unknown').toLowerCase()}-analysis-${marketData.date}`;

  const categoryIcons = {
    'Cryptocurrency': '‚Çø',
    'Forex': 'üí±',
    'Stock': 'üìä',
    'Commodity': 'ü•á'
  };
  const categoryIcon = categoryIcons[marketData.category] || 'üìà';

  const fullArticleHtml = `<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>${title} | DRMSI Trading Intelligence</title>
    <meta name="description" content="${metaDescription}">
    <meta name="keywords" content="${marketData.name}, ${marketData.symbol}, ${marketData.category}, trading analysis">
    <meta name="author" content="DRMSI Analysis Team">
    <link rel="canonical" href="https://drmsi.com/articles/${slug}.html">

    <meta property="og:type" content="article">
    <meta property="og:url" content="https://drmsi.com/articles/${slug}.html">
    <meta property="og:title" content="${title}">
    <meta property="og:description" content="${metaDescription}">
    <meta property="og:image" content="https://drmsi.com/og-image.jpg">

    <meta name="twitter:card" content="summary_large_image">
    <meta name="twitter:title" content="${title}">
    <meta name="twitter:description" content="${metaDescription}">

    <script type="application/ld+json">
    {
      "@context": "https://schema.org",
      "@type": "Article",
      "headline": "${title}",
      "author": {"@type": "Organization", "name": "DRMSI"},
      "publisher": {"@type": "Organization", "name": "DRMSI"},
      "datePublished": "${marketData.timestamp}"
    }
    </script>

    <style>
        * { margin: 0; padding: 0; box-sizing: border-box; }
        body { font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif; background: #0f0f0f; color: #fff; line-height: 1.7; }
        .container { max-width: 900px; margin: 0 auto; padding: 40px 20px; }
        .back-link { display: inline-block; color: #fbbf24; text-decoration: none; margin-bottom: 30px; }
        .article-meta { display: flex; gap: 20px; margin-bottom: 20px; font-size: 14px; color: #a0a0a0; }
        .price-badge { background: ${marketData.changePercent >= 0 ? '#10b981' : '#ef4444'}; color: white; padding: 4px 12px; border-radius: 4px; }
        h1 { font-size: 2.5rem; margin-bottom: 20px; }
        h2 { font-size: 1.8rem; margin: 40px 0 20px; color: #fbbf24; }
        p { margin-bottom: 20px; color: #d0d0d0; }
        .chart-container { margin: 40px 0; background: #1a1a1a; border-radius: 12px; overflow: hidden; }
        .risk-disclaimer { background: #1f1f1f; border-left: 4px solid #ef4444; padding: 20px; margin: 40px 0; border-radius: 4px; font-size: 14px; }
        @media (max-width: 768px) { h1 { font-size: 2rem; } .container { padding: 20px 15px; } }
    </style>
</head>
<body>
    <div class="container">
        <a href="/" class="back-link">‚Üê Back to DRMSI</a>
        <article>
            <div class="article-meta">
                <span>üìÖ ${new Date(marketData.timestamp).toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' })}</span>
                <span>${categoryIcon} ${marketData.category}</span>
                <span class="price-badge">${marketData.trendEmoji} ${marketData.changePercent}%</span>
            </div>
            ${aiContent}
            <div class="chart-container">
                <div style="height:500px;width:100%">
                    <div id="tradingview_${marketData.symbol}" style="height:100%;width:100%"></div>
                    <script src="https://s3.tradingview.com/tv.js"></script>
                    <script>
                    new TradingView.widget({
                        width: "100%",
                        height: "100%",
                        symbol: "${marketData.tradingviewSymbol}",
                        interval: "240",
                        timezone: "Etc/UTC",
                        theme: "dark",
                        style: "1",
                        locale: "en",
                        toolbar_bg: "#1a1a1a",
                        enable_publishing: false,
                        container_id: "tradingview_${marketData.symbol}"
                    });
                    </script>
                </div>
            </div>
        </article>
    </div>
</body>
</html>`;

  const summary = metaDescription.substring(0, 120);
  const shortArticleCard = `<article class="article-card" data-date="${marketData.timestamp}">
                    <div class="article-image">
                        <span>${categoryIcon}</span>
                    </div>
                    <div class="article-content">
                        <div class="article-meta">
                            <span>üìÖ ${new Date(marketData.timestamp).toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: 'numeric' })}</span>
                            <span>üè∑Ô∏è ${marketData.category}</span>
                        </div>
                        <h3 class="article-title">${title}</h3>
                        <p class="article-excerpt">${summary}...</p>
                        <a href="articles/${slug}.html" class="article-link">Read Analysis ‚Üí</a>
                    </div>
                </article>`;

    results.push({
      title: title,
      slug: slug,
      fullArticleHtml: fullArticleHtml,
      shortArticleCard: shortArticleCard,
      symbol: marketData.symbol,
      category: marketData.category,
      timestamp: marketData.timestamp,
      date: marketData.date,
      changePercent: marketData.changePercent,
      currentPrice: marketData.currentPrice
    });
});

return results;

// ===================================================================
// n8n Code Node: Merge Article Cards into index.html
// ===================================================================

const allItems = $input.all();

// Find index.html data from GitHub
const indexItem = allItems.find(item => 
  item.json && item.json.content && item.json.sha
);

if (!indexItem) {
  throw new Error('index.html data not found. Check connections.');
}

// Decode GitHub base64 content
const currentContent = Buffer.from(indexItem.json.content, 'base64').toString('utf-8');
const sha = indexItem.json.sha;

// Get all article cards from Format HTML node
const articleItems = allItems.filter(item => 
  item.json && item.json.shortArticleCard
);

console.log(`Processing ${articleItems.length} new article cards`);

if (articleItems.length === 0) {
  return [{
    json: {
      content: Buffer.from(currentContent).toString('base64'),
      sha: sha,
      message: 'No new articles to add'
    }
  }];
}

// Extract new cards
const newCards = articleItems.map(item => item.json.shortArticleCard);

// Find the articles grid in HTML
const gridStartMarker = '<div class="articles-grid" id="articlesGrid">';
const gridEndMarker = '</div><!-- articlesGrid -->';

const gridStart = currentContent.indexOf(gridStartMarker);
const gridEnd = currentContent.indexOf(gridEndMarker);

if (gridStart === -1 || gridEnd === -1) {
  throw new Error('Cannot find articles grid in index.html');
}

const gridOpenEnd = currentContent.indexOf('>', gridStart) + 1;

// Extract existing articles
const existingContent = currentContent.substring(gridOpenEnd, gridEnd);
const articleCardRegex = /<article class="article-card"[\s\S]*?<\/article>/g;
const existingCards = existingContent.match(articleCardRegex) || [];

console.log(`Found ${existingCards.length} existing cards`);

// Keep max 6 cards total (new ones first)
const maxCards = 6;
const cardsToKeep = existingCards.slice(0, Math.max(0, maxCards - newCards.length));
const allCards = [...newCards, ...cardsToKeep];

console.log(`Final: ${allCards.length} cards (${newCards.length} new + ${cardsToKeep.length} kept)`);

// Build new grid content with proper indentation
const newGridContent = '\n        ' + allCards.join('\n        ') + '\n      ';

// Reconstruct HTML
const beforeGrid = currentContent.substring(0, gridOpenEnd);
const afterGrid = currentContent.substring(gridEnd);
const updatedHTML = beforeGrid + newGridContent + afterGrid;

// Encode to base64 for GitHub
const base64Content = Buffer.from(updatedHTML).toString('base64');

return [{
  json: {
    content: base64Content,
    sha: sha,
    message: `üìä Updated ${newCards.length} article${newCards.length !== 1 ? 's' : ''}: ${articleItems.map(a => a.json.title).join(', ')}`,
    articleCount: allCards.length,
    newCount: newCards.length,
    keptCount: cardsToKeep.length
  }
}];
