const gridEndMarker = '</div><!-- articlesGrid -->';

const articlesGridStart = currentContent.indexOf(gridStartMarker);
const gridCloseTag = currentContent.indexOf(gridEndMarker);

if (articlesGridStart === -1 || gridCloseTag === -1) {
  throw new Error('Could not find articles grid section in index.html. Check HTML structure.');
}

// Find where the opening tag ends
const gridOpenTagEnd = currentContent.indexOf('>', articlesGridStart) + 1;

// ===================================================================
