// ===================================================================
const gridContent = currentContent.substring(gridOpenTagEnd, gridCloseTag);

// Regex to match complete article cards
const articleCardRegex = /<article class="article-card"[\s\S]*?<\/article>/g;
const existingCards = gridContent.match(articleCardRegex) || [];

